#!/usr/bin/env python# -*- coding: iso-8859-1 -*-from Tkinter import*import Tkinter#fenetrePrincipale # on hérite de Tkinter.Tk- classe de baseclass MoteurRecherche(Tkinter.Tk):                    def __init__(self, parent):                    Tkinter.Tk.__init__(self, parent)                    self.parent=parent                    self.interface= None                    self.initialize()                              def initialize(self):                    self.grid()                    self. textEntry= Tkinter.Text(self,width=84, height=1, bg="white",font=("Georgia", 14,),borderwidth=3,                                                  foreground='black',highlightcolor='orange')                    self.textEntry.grid( row= 1, column= 2,sticky='NS' )                    self.boutonCherche = Tkinter.Button( self, text = "Chercher", width=9,command=self.retourTxt)                    self.boutonCherche.grid(row =2, column= 2, sticky='NS')                    self.imgLogo = Tkinter.PhotoImage(file= "LogoWiki.gif")                    self.imageLogo = Tkinter.Label(self, image =self.imgLogo)                    self.imageLogo.grid( row=0, column=2,sticky='WE')                    #recupererTexte intro par l'utilisateur          def retourTxt(self):                    print (self.textEntry.get('1.0', END+'-1c'))#if __name__ == "__main__":app= MoteurRecherche(None)app.title('Moteur_Recherche')app.mainloop()